import{_ as r,W as o,X as s,Y as e,Z as a,$ as t,a0 as n,C as c}from"./framework-b947aea2.js";const d="/assets/2023-04-22-17-15-30-Screenshot_2023-04-22-17-14-59-377_com.MobileTick-055f607d.jpg",g="/assets/2023-04-22-17-27-12-1682155513761-e20bfddf.jpg",f="/assets/2023-04-22-17-27-34-1682155513748-27401e58.jpg",l="/assets/2023-04-22-17-31-39-1682155893329-9920565f.jpg",p="/assets/2023-04-22-17-31-46-1682155893343-ac764e8f.jpg",_="/assets/2023-04-22-17-34-55-1682156089554-1ba63abf.jpg",h="/assets/2023-04-22-17-35-02-1682156089589-cdcfb088.jpg",m="/assets/2023-04-22-17-37-14-1682156228724-1560f97a.jpg",u="/assets/2023-04-22-17-37-26-1682156228705-907562e2.jpg",b="/assets/2023-04-22-17-37-34-1682156228738-59b00443.jpg",x={},k=n('<h1 id="西昌——离成都三小时车程的桃源之地" tabindex="-1"><a class="header-anchor" href="#西昌——离成都三小时车程的桃源之地" aria-hidden="true">#</a> 西昌——离成都三小时车程的桃源之地</h1><h2 id="缘由" tabindex="-1"><a class="header-anchor" href="#缘由" aria-hidden="true">#</a> 缘由</h2><p>女朋友要到西昌办些事情，我作为陪同，也有幸来到此。我们赶了几乎是成都到西昌最晚的一班列车，晚上七点钟从成都出发，半夜十点才到达西昌。这趟列车很有意思是刷绿的高铁，我还是第一次乘坐这种列车。不知是否是我的个人原因，在列车的一路上的，我的耳膜总是胀的微微有些疼痛。</p><figure><img src="'+d+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h2 id="初识西昌" tabindex="-1"><a class="header-anchor" href="#初识西昌" aria-hidden="true">#</a> 初识西昌</h2><p>这是我第二次到达一个少数民族自治地区，刚下火车的我依旧被满大街的彝族文字震撼到，异域风情的感觉令我对“月城”西昌流连忘返。我们从高铁站出来后，打了一辆黑车，前往下榻的酒店。整顿好后，我们在楼下品尝了一番西昌的烤串。不得不说，和成都昂贵又难吃的烧烤相比，西昌烧烤简直让我对四川烧烤又有了一个新的认识。量大而实惠，口感干香爽口。</p><h2 id="邛海" tabindex="-1"><a class="header-anchor" href="#邛海" aria-hidden="true">#</a> 邛海</h2><p>女朋友需要办理事情，我这几天便独自在西昌自由活动起来。第一站便是邛海。</p>',8),E={href:"https://baike.baidu.com/item/%E6%B7%A1%E6%B0%B4%E6%B9%96/619544?fromModule=lemma_inlink",target:"_blank",rel:"noopener noreferrer"},B={href:"https://baike.baidu.com/item/%E6%96%AD%E9%99%B7%E6%B9%96?fromModule=lemma_inlink",target:"_blank",rel:"noopener noreferrer"},j={href:"https://baike.baidu.com/item/%E9%9B%86%E6%B0%B4%E9%9D%A2%E7%A7%AF/221156?fromModule=lemma_inlink",target:"_blank",rel:"noopener noreferrer"},y=n('<p>邛海确实是大，大到根本看不见边际，我在岸边买了张船票，得以泛舟于湖上，近距离观察邛海。</p><figure><img src="'+g+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="'+f+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p>泛舟之上，向远处眺望，天际线与湖水融为一体。对自然的敬畏油然而生，寄蜉蝣于天地，渺沧海之一粟！</p><figure><img src="'+l+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="'+p+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h1 id="泸山" tabindex="-1"><a class="header-anchor" href="#泸山" aria-hidden="true">#</a> 泸山</h1><p>与邛海仅挨着的便是泸山，值得一提的是，庐山上有特别多的野生猴子，作为游客还可以与它们近距离接触。</p><figure><img src="'+_+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="'+h+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h2 id="蓝花楹" tabindex="-1"><a class="header-anchor" href="#蓝花楹" aria-hidden="true">#</a> 蓝花楹</h2><p>四月的西昌，蓝花楹盛开，航天北路正是绝佳的打卡地点！</p><figure><img src="'+m+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="'+u+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="'+b+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure>',15);function z(A,M){const i=c("ExternalLinkIcon");return o(),s("div",null,[k,e("blockquote",null,[e("p",null,[a("邛海是四川省第二大"),e("a",E,[a("淡水湖"),t(i)]),a("，古称邛池，属更新世早期"),e("a",B,[a("断陷湖"),t(i)]),a("。其形状如蜗牛，南北长11.5公里，东西宽5.5公里，周长35公里，水域面积30平方公里左右；湖水平均深14米，最深处34米；水面标高为1507.14—1509.28米；水位变幅小，"),e("a",j,[a("集水面积"),t(i)]),a("约27平方公里。")])]),y])}const V=r(x,[["render",z],["__file","西昌.html.vue"]]);export{V as default};
